import React from 'react'
import styled from 'styled-components'
import Xnomad from './icons/Xnomad.svg'

const Wrapper = styled.div``

const Header = styled.div`
  display: flex;
  justify-content: left;
  align-items: center;
  height: 6vh;
  border-bottom: 2px solid black;
`
const CompanyIcon = styled.img`
  width: 9vw;
  height: 5vh;
`

const HeaderTextBoxes = styled.p`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-left: 3vw;
  font-weight: 500px;
  color: ${(p) => (p.id === 0 ? '#0f6b5c' : '')};
`

const headerTexts = ['MAKE IT MOVE IN READY', 'ABOUT YOU', 'PITCH YOUR IDEA']

const FirstView = styled.div`
  display: flex;
  height: 100vw;
  margin-top: 7vh;
`
const FirstViewLeft = styled.div`
  margin-left: 5vw;
`

const TitleText = styled.div`
  font-size: 30px;
  margin-bottom: 2vh;
`
const TitleSubText1 = styled.div`
  font-size: 20px;
  margin-bottom: 1.5vh;
`
const TitleSubText2 = styled.div`
  font-size: 12px;
  color: #0f6b5c;
`
const OptionDiv = styled.div`
  border: 1px solid #bcc1c3;
  width: 35vw;
  height: 11vh;
  display: flex;
  align-items: center;
  margin-top: 2vh;
  background: ${(p) => (p.active && !p.superActive ? '#0f6b5c' : '')};
  color: ${(p) => (p.active && !p.superActive ? 'white' : 'black')};
  &:hover {
    cursor: url('https://www.xnomad.co/static/media/LOGO_Name.bf44d7c0.svg'),
      auto;
  }
  background: ${(p) => (p.superActive ? '#0f6b5c' : '')};
  color: ${(p) => (p.superActive ? 'white' : 'black')};
`
const OptionDivLeft = styled.div`
  margin-left: 2vw;
  margin-right: 11.5vw;
`
const OptionDivLeftTitle = styled.div`
  //styleName: H6;
  font-family: Charter;
  font-size: 18px;
  font-weight: 700;
  line-height: 22px;
  letter-spacing: 0px;
  text-align: left;
`

const OptionDivLeftBody = styled.div`
  font-family: Fira Sans;
  font-size: 18px;
  line-height: 27px;
  letter-spacing: 0px;
  text-align: left;
`
const OptionDivRight = styled.div`
  margin-right: 2vw;
  min-width: 6vw;
`

const OptionDivTitles = [
  {
    titleText: 'I bring my own furniture',
    bodyText:
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris euismod duis nec nunc.',
    price: 0,
  },
  {
    titleText: '(S) Furniture package (0-80 m2)',
    bodyText:
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris euismod duis nec nunc.',
    price: '27 000',
  },
  {
    titleText: '(M) Furniture package  (80-200 m2)',
    bodyText:
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris euismod duis nec nunc.',
    price: '40 000',
  },
  {
    titleText: '(L) Furniture package (+200 m2)',
    bodyText:
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris euismod duis nec nunc.',
    price: '54 000',
  },
]

const FirstViewRight = styled.div``

export const App = () => {
  const [hoveredId, setHoveredId] = React.useState(null)
  const [clickedId, setClickedId] = React.useState(null)

  function superActive(clickedId, idx) {
    if (clickedId === idx) {
      setHoveredId(null)
    }
  }

  return (
    <Wrapper>
      <Header>
        <CompanyIcon src={Xnomad} />
        {headerTexts.map((item, idx) => {
          return (
            <HeaderTextBoxes id={idx}>
              {idx + 1}. {item}
            </HeaderTextBoxes>
          )
        })}
      </Header>
      <FirstView>
        <FirstViewLeft>
          <TitleText>Make your space move-in ready</TitleText>
          <TitleSubText1>Furniture + Design</TitleSubText1>
          <TitleSubText2>WHICH FURNITURE PACKAGE FITS YOU BEST?</TitleSubText2>
          {OptionDivTitles.map((item, idx) => {
            return (
              <OptionDiv
                id={idx + 10}
                onMouseEnter={() => setHoveredId(idx)}
                active={hoveredId === idx}
                onMouseLeave={() => setHoveredId(null)}
                // On-click I want to (1) set that element to its color, and (2) make sure that hoover is disabled
                onClick={() => setClickedId(idx)}
                superActive={clickedId === idx}
              >
                <OptionDivLeft>
                  <OptionDivLeftTitle>{item.titleText}</OptionDivLeftTitle>
                  <OptionDivLeftBody>{item.bodyText}</OptionDivLeftBody>
                </OptionDivLeft>
                <OptionDivRight>
                  {' '}
                  {idx !== 0 ? '+' : ''} SEK {item.price}
                </OptionDivRight>
              </OptionDiv>
            )
          })}
        </FirstViewLeft>
        <FirstViewRight></FirstViewRight>
      </FirstView>
    </Wrapper>
  )
}
